graphql.schema:
type Query {
  user(id: ID!): User
}

type User {
  id: ID!
  name: String!
  email: String!
}

type Query {
  user(id: ID!): User
  users(limit: Int, offset: Int): [User!]!
  post(id: ID!): Post
}

type User {
  id: ID!
  name: String!
  email: String!
}

type Post {
  id: ID!
  title: String!
  content: String
}

type Query {
  user(id: ID!): User
}

type Mutation {
  createUser(input: CreateUserInput!): User
  updateUser(id: ID!, input: UpdateUserInput!): User
  deleteUser(id: ID!): DeleteResult!
}

type User {
  id: ID!
  name: String!
  email: String!
  role: Role!
}

input CreateUserInput {
  name: String!
  email: String!
  role: Role
}

input UpdateUserInput {
  name: String
  email: String
  role: Role
}

type DeleteResult {
  success: Boolean!
  message: String
}

enum Role {
  ADMIN
  USER
  GUEST
}

type Query {
  user(id: ID!): User
}

type User {
  id: ID!
  name: String!
  email: String!
  profile: Profile
  posts: [Post!]!
}

type Profile {
  bio: String
  avatar: String
  settings: Settings
}

type Settings {
  theme: String
  notifications: Boolean
}

type Post {
  id: ID!
  title: String!
  content: String
  comments: [Comment!]!
}

type Comment {
  id: ID!
  text: String!
  author: User
}

type Query {
  users(limit: Int, offset: Int, role: Role): [User!]!
  tags: [String!]!
  numbers: [Int!]!
  matrix: [[Int!]!]!
}

type User {
  id: ID!
  name: String!
  tags: [String!]!
}

enum Role {
  ADMIN
  USER
  GUEST
}

type Query {
  user(id: ID!): User
  users: [User!]!
  restrictedData: SecretData
  unstableEndpoint: String
}

type User {
  id: ID!
  name: String!
  email: String!
}

type SecretData {
  secret: String!
}

type Query {
  user(id: ID!): User
}

type Mutation {
  createUser(name: String!, email: String!): User
  createOrder(productId: ID!, quantity: Int!): Order
}

type User {
  id: ID!
  name: String!
  email: String!
  createdAt: String!
}

type Order {
  id: ID!
  productId: ID!
  quantity: Int!
  timestamp: String!
  transactionRef: String!
}

type Query {
  fastQuery: String!
  slowQuery: String!
  verySlowQuery: String!
  user(id: ID!): User
}

type User {
  id: ID!
  name: String!
}

type Query {
  user(id: ID!): User
  usersByStatus(status: UserStatus!): [User!]!
  tasksByPriority(priority: Priority!): [Task!]!
}

type User {
  id: ID!
  name: String!
  status: UserStatus!
  role: Role!
}

type Task {
  id: ID!
  title: String!
  priority: Priority!
  status: TaskStatus!
}

enum UserStatus {
  ACTIVE
  INACTIVE
  PENDING
  SUSPENDED
}

enum Role {
  ADMIN
  MODERATOR
  USER
  GUEST
}

enum Priority {
  LOW
  MEDIUM
  HIGH
  CRITICAL
}

enum TaskStatus {
  TODO
  IN_PROGRESS
  REVIEW
  DONE
}

type Query {
  search(query: String!): [SearchResult!]!
  feed: [FeedItem!]!
}

union SearchResult = User | Post | Comment
union FeedItem = Post | Photo | Video

type User {
  id: ID!
  name: String!
  email: String!
}

type Post {
  id: ID!
  title: String!
  content: String
  author: User
}

type Comment {
  id: ID!
  text: String!
  author: User
}

type Photo {
  id: ID!
  url: String!
  caption: String
}

type Video {
  id: ID!
  url: String!
  duration: Int!
  title: String!
}

type Query {
  node(id: ID!): Node
  vehicles: [Vehicle!]!
  animals: [Animal!]!
}

interface Node {
  id: ID!
}

interface Vehicle {
  id: ID!
  brand: String!
  model: String!
  year: Int!
}

interface Animal {
  id: ID!
  name: String!
  species: String!
}

type Car implements Node & Vehicle {
  id: ID!
  brand: String!
  model: String!
  year: Int!
  doors: Int!
  fuelType: String!
}

type Motorcycle implements Node & Vehicle {
  id: ID!
  brand: String!
  model: String!
  year: Int!
  engineCC: Int!
}

type Dog implements Animal {
  id: ID!
  name: String!
  species: String!
  breed: String!
}

type Bird implements Animal {
  id: ID!
  name: String!
  species: String!
  canFly: Boolean!
}

type Query {
  users(filter: UserFilter, pagination: PaginationInput): UserConnection!
}

type Mutation {
  createProduct(input: CreateProductInput!): Product!
  updateProduct(id: ID!, input: UpdateProductInput!): Product!
}

input UserFilter {
  name: String
  email: String
  status: UserStatus
  roles: [Role!]
  createdAfter: String
  createdBefore: String
}

input PaginationInput {
  limit: Int
  offset: Int
  sortBy: String
  sortOrder: SortOrder
}

input CreateProductInput {
  name: String!
  description: String
  price: Float!
  category: CategoryInput!
  tags: [String!]
  metadata: MetadataInput
}

input UpdateProductInput {
  name: String
  description: String
  price: Float
  category: CategoryInput
  tags: [String!]
}

input CategoryInput {
  name: String!
  parentId: ID
}

input MetadataInput {
  sku: String
  weight: Float
  dimensions: DimensionsInput
}

input DimensionsInput {
  width: Float!
  height: Float!
  depth: Float!
}

enum UserStatus {
  ACTIVE
  INACTIVE
}

enum Role {
  ADMIN
  USER
}

enum SortOrder {
  ASC
  DESC
}

type UserConnection {
  nodes: [User!]!
  totalCount: Int!
  pageInfo: PageInfo!
}

type User {
  id: ID!
  name: String!
  email: String!
}

type PageInfo {
  hasNextPage: Boolean!
  hasPreviousPage: Boolean!
}

type Product {
  id: ID!
  name: String!
  description: String
  price: Float!
  category: Category!
  tags: [String!]!
}

type Category {
  id: ID!
  name: String!
}

type Query {
  messages: [Message!]!
}

type Subscription {
  messageAdded(channel: String!): Message!
  userStatusChanged(userId: ID!): UserStatus!
  countdown(from: Int!): Int!
}

type Message {
  id: ID!
  text: String!
  channel: String!
  timestamp: String!
}

type UserStatus {
  userId: ID!
  status: Status!
  lastSeen: String!
}

enum Status {
  ONLINE
  OFFLINE
  AWAY
}

type Query {
  ping: String!
}

type Subscription {
  heartbeat: Heartbeat!
  randomUpdates: Update!
  repeatingAlerts: Alert!
}

type Heartbeat {
  timestamp: String!
  sequence: Int!
}

type Update {
  id: ID!
  value: Float!
}

type Alert {
  level: String!
  message: String!
}

type Query {
  user(id: ID!): User
  secretData: String!
}

type User {
  id: ID!
  name: String!
}

directive @deprecated(reason: String) on FIELD_DEFINITION | ENUM_VALUE
directive @auth(requires: Role!) on FIELD_DEFINITION
directive @rateLimit(limit: Int!, window: String!) on FIELD_DEFINITION

type Query {
  user(id: ID!): User
  adminData: AdminData @auth(requires: ADMIN)
  publicEndpoint: String @rateLimit(limit: 100, window: "1m")
  oldEndpoint: String @deprecated(reason: "Use newEndpoint instead")
  newEndpoint: String
}

type User {
  id: ID!
  name: String!
  email: String!
  oldField: String @deprecated(reason: "This field is no longer used")
}

type AdminData {
  secrets: [String!]!
}

enum Role {
  ADMIN
  USER
  GUEST
}

type Query {
  product(id: ID!): Product
  products(filter: ProductFilter, pagination: PaginationInput): ProductConnection!
  categories: [Category!]!
  cart(userId: ID!): Cart
  order(id: ID!): Order
}

type Mutation {
  addToCart(userId: ID!, productId: ID!, quantity: Int!): Cart!
  removeFromCart(userId: ID!, productId: ID!): Cart!
  checkout(userId: ID!, payment: PaymentInput!): Order!
  cancelOrder(orderId: ID!): Order!
}

type Subscription {
  orderStatusChanged(orderId: ID!): OrderStatusUpdate!
  inventoryUpdate(productId: ID!): InventoryUpdate!
}

type Product {
  id: ID!
  name: String!
  description: String
  price: Float!
  currency: Currency!
  category: Category!
  images: [String!]!
  inventory: Int!
  rating: Float
  reviews: [Review!]!
}

type Category {
  id: ID!
  name: String!
  slug: String!
  products: [Product!]!
}

type Cart {
  id: ID!
  userId: ID!
  items: [CartItem!]!
  total: Float!
  currency: Currency!
}

type CartItem {
  product: Product!
  quantity: Int!
  subtotal: Float!
}

type Order {
  id: ID!
  userId: ID!
  items: [OrderItem!]!
  status: OrderStatus!
  total: Float!
  currency: Currency!
  shippingAddress: Address!
  createdAt: String!
  updatedAt: String!
}

type OrderItem {
  product: Product!
  quantity: Int!
  price: Float!
}

type Address {
  street: String!
  city: String!
  state: String!
  zipCode: String!
  country: String!
}

type Review {
  id: ID!
  rating: Int!
  comment: String
  author: String!
  createdAt: String!
}

type OrderStatusUpdate {
  orderId: ID!
  status: OrderStatus!
  timestamp: String!
  message: String
}

type InventoryUpdate {
  productId: ID!
  available: Int!
  reserved: Int!
}

input ProductFilter {
  categoryId: ID
  minPrice: Float
  maxPrice: Float
  inStock: Boolean
  search: String
}

input PaginationInput {
  limit: Int
  offset: Int
  sortBy: ProductSortField
  sortOrder: SortOrder
}

input PaymentInput {
  method: PaymentMethod!
  cardToken: String
}

enum Currency {
  USD
  EUR
  GBP
}

enum OrderStatus {
  PENDING
  CONFIRMED
  PROCESSING
  SHIPPED
  DELIVERED
  CANCELLED
}

enum ProductSortField {
  NAME
  PRICE
  RATING
  CREATED_AT
}

enum SortOrder {
  ASC
  DESC
}

enum PaymentMethod {
  CREDIT_CARD
  DEBIT_CARD
  PAYPAL
}

type ProductConnection {
  nodes: [Product!]!
  totalCount: Int!
  pageInfo: PageInfo!
}

type PageInfo {
  hasNextPage: Boolean!
  hasPreviousPage: Boolean!
  startCursor: String
  endCursor: String
}
